// Advanced lead scoring algorithm with error handling
try {
  const inputData = $input.all();

  // Always return at least one item to prevent workflow stops
  if (!inputData || inputData.length === 0) {
    return [{ json: { 
      error: "No input data received", 
      leads_processed: 0,
      status: "no_input",
      timestamp: new Date().toISOString()
    } }];
  }

  const response = $input.first().json;
  console.log('Raw response:', JSON.stringify(response, null, 2));

  // Multiple ways to extract leads data
  let leads = [];
  if (response && response.items) {
    leads = response.items;
  } else if (response && response.data) {
    leads = response.data;
  } else if (Array.isArray(response)) {
    leads = response;
  } else if (response) {
    leads = [response];
  }

  console.log('Extracted leads:', leads.length, 'items');

  if (!leads || leads.length === 0) {
    return [{ json: { 
      message: "No leads found to process", 
      leads_processed: 0, 
      status: "no_leads",
      raw_response: response,
      timestamp: new Date().toISOString()
    } }];
  }

  const scoredLeads = [];

  for (const lead of leads) {
  let score = 0;
  let scoreBreakdown = {
    demographic: 0,
    behavioral: 0,
    engagement: 0,
    firmographic: 0,
    temporal: 0
  };

  // DEMOGRAPHIC SCORING (0-25 points)
  if (lead.job_title) {
    const title = lead.job_title.toLowerCase();
    if (title.includes('ceo') || title.includes('founder') || title.includes('president')) {
      scoreBreakdown.demographic += 25;
    } else if (title.includes('vp') || title.includes('director') || title.includes('head')) {
      scoreBreakdown.demographic += 20;
    } else if (title.includes('manager') || title.includes('lead')) {
      scoreBreakdown.demographic += 15;
    } else {
      scoreBreakdown.demographic += 10;
    }
  }

  // FIRMOGRAPHIC SCORING (0-25 points)
  if (lead.company_size) {
    if (lead.company_size >= 1000) scoreBreakdown.firmographic += 25;
    else if (lead.company_size >= 100) scoreBreakdown.firmographic += 20;
    else if (lead.company_size >= 50) scoreBreakdown.firmographic += 15;
    else if (lead.company_size >= 10) scoreBreakdown.firmographic += 10;
    else scoreBreakdown.firmographic += 5;
  }

  // Industry scoring
  if (lead.industry) {
    const highValueIndustries = ['technology', 'saas', 'finance', 'healthcare', 'consulting'];
    if (highValueIndustries.some(ind => lead.industry.toLowerCase().includes(ind))) {
      scoreBreakdown.firmographic += 10;
    }
  }

  // BEHAVIORAL SCORING (0-30 points)
  if (lead.website_visits) {
    if (lead.website_visits >= 10) scoreBreakdown.behavioral += 15;
    else if (lead.website_visits >= 5) scoreBreakdown.behavioral += 10;
    else if (lead.website_visits >= 2) scoreBreakdown.behavioral += 5;
  }

  if (lead.pages_viewed) {
    if (lead.pages_viewed >= 20) scoreBreakdown.behavioral += 15;
    else if (lead.pages_viewed >= 10) scoreBreakdown.behavioral += 10;
    else if (lead.pages_viewed >= 5) scoreBreakdown.behavioral += 5;
  }

  // ENGAGEMENT SCORING (0-20 points)
  if (lead.email_opens) {
    if (lead.email_opens >= 5) scoreBreakdown.engagement += 10;
    else if (lead.email_opens >= 2) scoreBreakdown.engagement += 5;
  }

  if (lead.email_clicks) {
    if (lead.email_clicks >= 3) scoreBreakdown.engagement += 10;
    else if (lead.email_clicks >= 1) scoreBreakdown.engagement += 5;
  }

  if (lead.downloads) {
    scoreBreakdown.engagement += Math.min(lead.downloads * 3, 10);
  }

  // TEMPORAL SCORING - Recent activity bonus (0-10 points)
  const now = new Date();
  const lastActivity = new Date(lead.last_activity_at || lead.created_at);
  const daysSinceActivity = (now - lastActivity) / (1000 * 60 * 60 * 24);

  if (daysSinceActivity <= 1) scoreBreakdown.temporal += 10;
  else if (daysSinceActivity <= 3) scoreBreakdown.temporal += 7;
  else if (daysSinceActivity <= 7) scoreBreakdown.temporal += 5;
  else if (daysSinceActivity <= 14) scoreBreakdown.temporal += 3;

  // NEGATIVE SCORING
  if (lead.unsubscribed) score -= 50;
  if (lead.bounced_emails && lead.bounced_emails > 2) score -= 20;
  if (daysSinceActivity > 90) score -= 20;

  // Calculate final score
  score = Object.values(scoreBreakdown).reduce((sum, val) => sum + val, 0);
  score = Math.max(0, Math.min(100, score)); // Cap between 0-100

  // Determine lead temperature
  let temperature;
  let nextAction;

  if (score >= 80) {
    temperature = 'hot';
    nextAction = 'immediate_sales_contact';
  } else if (score >= 60) {
    temperature = 'warm';
    nextAction = 'personalized_follow_up';
  } else if (score >= 40) {
    temperature = 'cold';
    nextAction = 'nurturing_sequence';
  } else {
    temperature = 'frozen';
    nextAction = 're_engagement_campaign';
  }

  scoredLeads.push({
    ...lead,
    previous_score: lead.score,
    score: score,
    score_breakdown: scoreBreakdown,
    lead_temperature: temperature,
    recommended_action: nextAction,
    score_updated_at: now.toISOString(),
    score_change: score - (lead.score || 0)
  });
  }

  console.log('Processed', scoredLeads.length, 'leads');
  return scoredLeads.map(lead => ({ json: lead }));

} catch (error) {
  console.error('Error in lead scoring:', error);
  return [{ json: { 
    error: error.message, 
    status: "error",
    timestamp: new Date().toISOString()
  } }];
}

